mapper.select:
select id, write_date, modify_date, user_name, title, main_text
from post
where id = #{id}

select id, write_date, modify_date, user_name, title, main_text
from post
where user_name = #{userName}

mapper.update:
update post
set title = #{dto.title}
    main_text = #{dto.mainText}
    modify_date = #{modifyDate}
where id = #{id}
